DROP TABLE IF EXISTS `xt_admin`;
CREATE TABLE `xt_admin` (
  `aid` int(11) NOT NULL AUTO_INCREMENT COMMENT '管理员ID',
  `a_name` varchar(200) DEFAULT NULL COMMENT '管理员姓名',
  `a_account` varchar(200) NOT NULL DEFAULT '' COMMENT '管理员帐号',
  `a_password` varchar(200) NOT NULL DEFAULT '' COMMENT '管理员密码',
  `a_memo` text COMMENT '备注',
  `a_type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '1 超级管理员（可以管理所有功能，也可以审核），2 审核员（可以编辑并审核资料，将资料发布），3 普通编辑人员（只能编辑，不能审核）',
  `a_audits` varchar(4000) NOT NULL COMMENT '审核员和普通编辑人员都可以被限定可操作的频道，频道使用json记录，如：{"oneself":0,"r":"38,40,43","w":"35,36,37","d":"38","a":"38,40,4336,37"}',
  `a_avata` varchar(100) NOT NULL COMMENT '头像路径',
  `a_status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '管理员的状态，1 正常；0 停用',
  `a_dt_add` datetime NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`aid`),
  KEY `admin_id` (`aid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

