  `au_dt_add` datetime DEFAULT NULL,
  `au_result` tinyint(4) DEFAULT '0',
  `au_memo` text,
  `cid` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`auid`),
  KEY `au_cid` (`cid`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

