DROP TABLE IF EXISTS `xt_channel`;
CREATE TABLE `xt_channel` (
  `chid` int(11) NOT NULL AUTO_INCREMENT COMMENT '频道ID',
  `ch_name` varchar(200) NOT NULL DEFAULT '' COMMENT '频道名称',
  `ch_order` int(11) DEFAULT NULL COMMENT '排序',
  `ch_father_id` int(11) NOT NULL DEFAULT '0' COMMENT '父频道ID，如果为0就是根频道',
  `ch_audit` tinyint(11) NOT NULL DEFAULT '0' COMMENT '是否需要审核，0 不需要，1 需要',
  `ch_memo` text COMMENT '频道介绍',
  `ch_ext` varchar(200) DEFAULT NULL COMMENT '外接功能的名称，如：test。自动对应pages/ext_test.php。',
  PRIMARY KEY (`chid`),
  KEY `channel_id` (`chid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

