DROP TABLE IF EXISTS `xt_content`;
CREATE TABLE `xt_content` (
  `cid` int(11) NOT NULL AUTO_INCREMENT,
  `chid` int(11) DEFAULT NULL,
  `c_title` varchar(200) NOT NULL COMMENT '主标题',
  `c_title2` varchar(200) DEFAULT NULL COMMENT '副标题',
  `c_author` varchar(50) DEFAULT NULL COMMENT '作者',
  `c_source` varchar(100) DEFAULT NULL COMMENT '来源',
  `c_order` int(11) NOT NULL DEFAULT '0' COMMENT '排序号，数字越大越排在前面，默认都是0',
  `c_level` tinyint(4) NOT NULL DEFAULT '0' COMMENT '资料等级，0 普通，1推荐，2置顶',
  `c_audit` tinyint(4) NOT NULL DEFAULT '1' COMMENT '资料审核状态，0 待审核，1 审核通过或自动发布，-1 审核未通过',
  `c_aid_add` int(11) DEFAULT NULL COMMENT '添加者，管理员id',
  `c_aid_update` int(11) DEFAULT NULL COMMENT '修改者，管理员id',
  `c_dt_add` datetime DEFAULT NULL COMMENT '添加时间',
  `c_dt_update` datetime DEFAULT NULL COMMENT '修改时间',
  `c_summary` text COMMENT '资料简介',
  `c_detail` text COMMENT '资料详情',
  `c_pic` varchar(200) DEFAULT NULL COMMENT '头图',
  `c_pic_url` varchar(1000) DEFAULT NULL COMMENT '头图关联的网址，有时候图片需要有关联网址的设定',
  `c_attachment` tinyint(4) NOT NULL DEFAULT '0' COMMENT '附件数量，附件另外记录到附件表中，这个记录的是具体的数量',
  PRIMARY KEY (`cid`),
  KEY `content_default` (`chid`,`c_audit`,`c_level`,`c_order`,`cid`) USING BTREE,
  KEY `content_aid_add` (`c_aid_add`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='cms资料内容管理表';

INSERT INTO `xt_content` (`cid`, `chid`, `c_title`, `c_title2`, `c_author`, `c_source`, `c_order`, `c_level`, `c_audit`, `c_aid_add`, `c_aid_update`, `c_dt_add`, `c_dt_update`, `c_summary`, `c_detail`, `c_pic`, `c_pic_url`, `c_attachment`) VALUES
(37,	74,	'卖火柴的小女孩',	'',	'',	'',	0,	0,	10,	1,	1,	'2019-06-06 12:31:38',	'2019-06-11 15:42:53',	'',	'&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span style=&quot;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;铃儿响叮当&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;【音乐起，在音乐中，有数人过场，卖火柴的小女孩在叫卖】&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span style=&quot;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;钟声&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; OS：教堂的钟声伴随着圣诞歌在夜空中回荡，漫天的大雪飄落在大街小巷。一个小女孩为了给妈妈治 病，整整一天沿街吆喝着卖火柴，可是却连一根火柴也没有卖掉，在凛冽的寒风中，她划亮着手中的 火柴。闪烁的火苗，燃起小女孩心中一个又一个美丽的遐想......&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【0S 结束，小女孩继续叫卖，一个有钱人的富女孩抱着一个大洋娃娃上场】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#FFE500;font-size:14px;&quot;&gt;富女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;妈妈，你快点呀!我们快回家看看爸爸给我买什么圣诞礼物了。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【贵妇人上场】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#DFC5A4;font-size:14px;&quot;&gt;贵妇人：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;来了，来了，看你急的，爸爸一定会给你买礼物的。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#FFE500;font-size:14px;&quot;&gt;富女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;妈妈，爸爸会给我买什么呢?&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#DFC5A4;font-size:14px;&quot;&gt;贵妇人：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;哈哈.我怎么猜得到呢? &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【卖火柴的小女孩走到富女孩身边，羡慕地看着她抱着的娃娃，欲用手去摸】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#FFE500;font-size:14px;&quot;&gt;富女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;妈妈你看她。(逃到妈妈身边) &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#DFC5A4;font-size:14px;&quot;&gt;贵妇人：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(对卖火柴的小女孩)快滚!又臭又脏的东西，(对富女孩)孩子，别理这些穷鬼!我们走。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【二人下。卖火柴的小女孩看着她们远去】&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【一绅士上，卖火柴的小女孩快步走到他面前】&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;先生，请买一根火柴吧，&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#64451D;color:#FFFFFF;font-size:14px;&quot;&gt;绅士：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;不要!&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(挡住绅士)先生买一根吧!&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#64451D;color:#FFFFFF;font-size:14px;&quot;&gt;绅士：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(踢卖火柴的小女孩)滚!【下】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;我该怎么办啊?我连一根还没有卖掉，我要是现在回家了，爸爸一定会打我的。 【一只漂亮的皮球滚到卖火柴的小女孩脚边，卖火柴的小女孩拾起，一小女孩上 】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(跑到卖火柴的小女孩的前面)这是我的球。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;给你。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;谢谢!(欲走，发现什么似的)咦，那么你在干什么?&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;我在卖火柴。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;你穿得那么少，你不冷吗?&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;我很冷，我已经把所有的衣服都穿在身上了。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;那你叫你妈妈帮你买衣服呀。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;我们哪有钱买衣服啊。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;你有那么多火柴，为什么不划一根来暖和你的手呢? &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;这火柴是要卖钱给妈妈治病的，妈妈还在家等我卖火柴的钱呢!......块看!流星!&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;在哪?&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt; 刚刚划过去了。奶奶说过，当有一颗流星划过就有一个人的灵魂去了天堂。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(思索着)天堂?&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;对!天堂。哎，你能买我一根火柴吗? &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;可以啊!我把你的火柴全买下来，你就可以早点回家，早点让你妈妈去看病......(小女孩找遍 所有的口袋，遗憾地说.可是我没有带钱。哦!不过我可以让我妈妈把你的火柴全买下来。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;真的吗? &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(急切地)真的，真的，我妈妈是一个善良的人。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(高兴地)那好!我就在这里等你回来。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【卖火柴的小女孩数着火柴，兴奋无比】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;她怎么还没来呀!(继续叫卖)卖火柴......路上的人越来越少了，他们都在准备过圣诞 节。可我现在一根火柴也没有卖掉。(来回跑着取暖)好冷啊!我就划一根火柴取取暖吧!&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;圣诞周&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【音乐起，在梦中她看见了几只烤鹅向她走来，她追逐着，但始终无法得到烤鹅】 【音乐止，烤鹅下】 【空旷的舞台上，只剩下卖火柴的女孩一人，她四处寻找鹅】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;你们别走，别走啊! &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;A大调华尔兹&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;【沮丧地坐在地上，又拿出一根火柴划亮(音乐起)】 卖火柴的小女孩：奶奶，奶奶。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【另一演区，奶奶与卖火柴的小女孩出现】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#EE33EE;font-size:14px;&quot;&gt;奶奶：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;我的孩子。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;奶奶!&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#EE33EE;font-size:14px;&quot;&gt;奶奶：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;我的孩子，那么冷的天你还在卖火柴，你受苦了。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;奶奶您別走，我知道火柴一灭您就会不见了。就像那美味的烤鹅一样，奶奶您带我 走吧!&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#EE33EE;font-size:14px;&quot;&gt;奶奶：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;孩子，奶奶会带你走的。你现在要坚强地活下去。 【奶奶亲抚摸着卖火柴的小女孩的脸庞，拥抱着她】&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#EE33EE;font-size:14px;&quot;&gt;奶奶：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;孩子，我要走了。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(着急地)奶奶，您别走，您永远留下来陪我，好吗? &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#EE33EE;font-size:14px;&quot;&gt;奶奶：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;不!我现在不能留下来陪你。 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#60D978;font-size:14px;&quot;&gt;卖火柴的小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;那您带我走吧!我要永远和您在一起!奶奶，您就带我一起走吧! 卖火柴的小女孩：(痛苦地，大声地)奶奶，奶奶......&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;小广板&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:14px;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; (音乐起)&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【向着奶奶远去的方向在地上爬着，爬着，倒下】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;(匆匆上)小姐姐!小姐姐!我来了，我带了好多钱.(推了推卖火柴的小女孩)小姐姐，小 姐姐...&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【音乐起，小女孩发现卖火柴的小女孩死了，将自己的围巾给卖火柴的小女孩盖上】&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【音乐中 6-8 名天使的跑到卖火柴的小女孩的身旁缓缓地转身，看着远方】&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; OS：她走了。她到另一个世界去了。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【女孩走到舞台中央，看着天空】 &lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#00D5FF;font-size:14px;&quot;&gt;小女孩：&lt;/span&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;快看!流星!(缓缓地)她说过，当有一颗流星划过的时候，就有一个人的灵魂去了天堂。小 姐姐去了天堂。(喊)我祝福你!&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt; 【音乐至高潮，切光】&lt;/span&gt; \r\n&lt;/p&gt;',	'',	'',	5),
(38,	74,	'阳光总在风雨后',	'',	'',	'',	0,	0,	10,	1,	1,	'2019-06-06 18:24:33',	'2019-06-11 15:51:41',	'',	'作者：洪亮&lt;br /&gt;\r\n人物：赵前（父亲，对越自卫反击战老兵，退休，腿负过伤行动不便），孙力（儿子，警察），团团（儿子女友，社区志愿者）&lt;br /&gt;\r\n时间：周六上午，晴&lt;br /&gt;\r\n地点：赵前家&lt;br /&gt;\r\n事件：儿子带女友来探望父亲&lt;br /&gt;\r\n&lt;br /&gt;\r\n第一场&lt;br /&gt;\r\n（场景）一早赵老头买菜回家，进门后收拾房间，做一些准备工作等待儿子带女朋友来探望。（行动不便，一瘸一拐）&lt;br /&gt;\r\n（独白）&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;儿子昨晚来电话，说今天带女朋友来看我。这小子交了女朋友也不告诉我。说上门就上门，搞的我比他还紧张。&lt;br /&gt;\r\n（场景）赵老头继续整理客厅。（表现出坐立不安，纠结于物件的摆放）&lt;br /&gt;\r\n（此时电话响起）&lt;br /&gt;\r\n赵老头摸索一番后拿起电话，电话内响起男声。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#DFC5A4;&quot;&gt;（画外音）&lt;/span&gt;你好，请问是赵前吗？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;是的，您哪位？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#DFC5A4;&quot;&gt;（画外音）&lt;/span&gt;这里是徐汇区某某街道派出所，我是刘警官。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;请问您有什么事吗？（小心翼翼问到）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#DFC5A4;&quot;&gt;（画外音）&lt;/span&gt;你涉嫌一起诈骗案，我们现在需要你来派出所协助调查。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;啊？你们是不是搞错了啊？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#DFC5A4;&quot;&gt;（画外音）&lt;/span&gt;你的身份证后四位是不是0028？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;对啊！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#DFC5A4;&quot;&gt;（画外音）&lt;/span&gt;那就没错了！这样吧，你带好身份证和银行卡来我这一趟。我们需要你提供银行流水来协助调查。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;沉思片刻后道：好，那这样吧，我让我儿子给你拿去。这不巧了么，他正好也是你们所里的民警。你现在在哪儿，我让他马上过去。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#DFC5A4;&quot;&gt;（画外音）&lt;/span&gt;你这个糟老头子，坏的很！（福建口音）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;模仿福建口音道：气死你，气死你，怎莫样？（随后挂断电话）&lt;br /&gt;\r\n（&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;面向观众道）还想骗我？你还嫩着呢！儿子说过，只要陌生电话，来电提到银行卡的，准是骗子！十枪中十靶，没得跑！&lt;br /&gt;\r\n&lt;br /&gt;\r\n第二场&lt;br /&gt;\r\n（场景）赵老头一瘸一拐的继续整理买回来的菜，这时敲门声响起。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;吓一跳：谁啊？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力：&lt;/span&gt;爸，是我啊！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;噢，来了来了！（赵老头一瘸一拐的去开门）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力&lt;/span&gt;隔着门道：爸，您慢点儿！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团：&lt;/span&gt;你怎么回家钥匙都不带啊？&lt;br /&gt;\r\n（场景）赵老头打开门，孙力进门赶紧扶老头去桌子边坐下。女朋友拿着礼物和菜跟着进门。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力&lt;/span&gt;把女友拉到跟前介绍道：爸，这是我女朋友，团团。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;看着团团笑道：好好好，闺女长的好看，名字也好听。团团圆圆，听起来像国宝大熊猫的名字，哈哈哈。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力：&lt;/span&gt;爸，您就爱开玩笑。不过她在我心里就是大熊猫，呵呵呵。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团&lt;/span&gt;害羞的戳了一下孙力的腰，来到赵老头面前。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团：&lt;/span&gt;叔叔您好，我叫萧团团，您叫我团团就行了。这次孙力临时带我来看您，我实在没准备，真不好意思。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;没事没事，这小子哪次来不是临时决定的。我都习惯了。哎，你也知道，咱小力是做缉毒警的，时间不定，你可也要多担待啊！来来来，都坐下吧！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团：&lt;/span&gt;嗯&lt;br /&gt;\r\n（三人各自坐好）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力&lt;/span&gt;看到桌子上刚买回的菜，语气微带责备的说道：爸，我不是和你说别去买菜嘛。您怎么又不听话呢，您忘了上次您去买菜摔了一觉，床上躺了一个月呢。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;乐呵道：这不是听说你带女朋友来嚒，我想亲自下厨做几个菜。平时啊，都有社区服务，每天送饭上门，我也没机会下厨。这次团团来了，我总不能让她跟着我吃盒饭吧？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团：&lt;/span&gt;叔叔，我们也带了菜了。您就别动了，今天就让孙力去厨房大现身手吧。我陪您坐着聊聊天。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力：&lt;/span&gt;行，今天就让我来表现一下吧！平时想表现还没这机会呢！（说完拿起菜，下场）&lt;br /&gt;\r\n&lt;br /&gt;\r\n第三场&lt;br /&gt;\r\n（场景）赵老头和团团做在桌旁，对视尬笑，试图找话题闲聊。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团：&lt;/span&gt;叔叔，听孙力说您之前摔了一觉，都康复了吗？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;闻言起身展示道：好着呢！你看，我身子硬朗，都能打套军体拳了！&lt;br /&gt;\r\n说着比划了一下， 但站立不稳，立马坐倒。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团&lt;/span&gt;见状立马上前扶住老头道：叔叔，您的腿还没恢复，当心点。我家认识一个有名的骨科医生。回头我带您去看看吧！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;按着腿，缓缓道：不用拉，叔叔这腿是好不了喽。这是当年参加对越自卫反击战时留下的。（骄傲）比起永远留在那里的战友，这点伤算不得什么。（深沉）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团&lt;/span&gt;默默的坐回座位，看着赵老的腿若有所思。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;见状尬笑道：不说这些啦。团团，你是怎么认识小力的呀？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团&lt;/span&gt;收拾心情道：我和小力都是社区志愿者，是在一次敬老演出的活动中认识的。当时他在宣传防电话诈骗，而我临时客串他的搭档，演一个骗子。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;笑道：哈哈哈，这就是缘分啊。说来巧了，我刚才还接到一个诈骗电话呢。还好你赵叔叔我防范意识强，让他呀，急到骂娘！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团&lt;/span&gt;跟着笑道：叔叔，您真幽默。像个老玩童，呵呵呵！咦？！叔叔，您刚才说您姓赵？不对啊，小力是姓孙啊！您怎么会姓赵呢？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;一下沉默，拿起杯子喝了一口水，放下杯子缓缓道：闺女啊，其实小力不是我儿子。我儿子叫赵武，也是一名缉毒警察。三年前的一个晚上，他紧急出任务。可是没想到，他这一去，就在也没有回来！我老伴走的早，就留下这个儿子，没想到就这么没了！！！小力是他的战友，这几年都是他在代替我儿子照顾我。我呢，也把他当自己孩子。如果没有他在，我都不知道怎么活过来！可是我心里不好受啊！小力把他有限的时间都用来照顾我，结果把他自己的事给耽误了。今天，你能来，叔叔真的很开心，很开心啊！（赵老头边说边流泪）&lt;br /&gt;\r\n&lt;br /&gt;\r\n第四场&lt;br /&gt;\r\n（场景）赵老头说到伤心处默默流泪，团团立马递上纸巾并安慰道。这时孙力从厨房出来上场。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力&lt;/span&gt;紧张道：爸，您怎么了？刚才还在厨房听你笑呢！怎么一下就哭了呢？团团，你干嘛了？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;团团&lt;/span&gt;不知所措：对，对不起！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;抹完泪道：不关团团的事，是我今天太开心了，喜极而泣！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;拉过孙力道：小力，这些天我一直在考虑一件事。今天啊，我终于有了个决定。我准备搬去社区敬老院住。那里设施条件好，我住的也省心。街道里的王主任已经帮我联系过了，说我符合条件，就等我做决定了&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力：&lt;/span&gt;爸，您不是一直舍不得这个家嚒？您说，只要这个家在，小武哥他就一定会回来。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;拍拍孙力的背道：是我太执念了！小武为国家牺牲，这是他的光荣。当年我没做到的，他做到了！作为一个老兵，我为有这样一个儿子而骄傲！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力&lt;/span&gt;抱住赵老头道：爸，您能这么想，小武哥走的也就安心了！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头：&lt;/span&gt;小力，这些年，谢谢你了。从今开始，你们也不用老来看我了。去了敬老院后有的是老太太陪我聊天。我呀，以后想打麻将也不会一缺三了！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;孙力&lt;/span&gt;拉过团团道：爸，您永远是我爸，我们也永远是您的孩子。团团她是社区志愿者，我没空的时候她也会经常来看您，陪您聊天。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#00D5FF;&quot;&gt;赵老头&lt;/span&gt;，拉住他俩的手道：好，我相信我们都会快乐的生活下去。不管之前发生过多少不幸，我们要坚信，阳光总在风雨后。&lt;br /&gt;\r\n（音乐起）&lt;br /&gt;\r\n&lt;p&gt;\r\n	结束曲（阳光总在风雨后）\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;阳光总在风雨后&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;br /&gt;\r\n完&lt;br /&gt;',	'',	'',	1),
(39,	74,	'家和万事兴',	'',	'',	'',	0,	0,	10,	1,	1,	'2019-06-06 18:56:34',	'2019-06-26 13:41:49',	'',	'报幕介绍（陈建负责）：&lt;br /&gt;\r\n剧目名称：家和万事兴&lt;br /&gt;\r\n演员介绍： &lt;br /&gt;\r\n&lt;br /&gt;\r\n第一幕：&lt;br /&gt;\r\n咨询师：（灯关只照在咨询师那里）有这样的一家人，他们的故事是这样的。（下台到旁边。）&lt;br /&gt;\r\n&lt;br /&gt;\r\n第二幕：&lt;br /&gt;\r\n开场：（灯光切换到表演者区域）&lt;br /&gt;\r\n&lt;p&gt;\r\n	一个&lt;span style=&quot;background-color:#FFE500;&quot;&gt;女孩&lt;/span&gt;在桌子边写作业。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	一个&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;女人&lt;/span&gt;在旁一边盯着一边指点：“看你的作业，这个又错了，怎么回事？就不能专心吗？”。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	这时&lt;span style=&quot;background-color:#60D978;&quot;&gt;男人&lt;/span&gt;打开门进来：“老婆，我回来了。”\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;女人&lt;/span&gt;抬起头，指着刚进门的拉着行李箱的男人骂：“你还知道回来，每个月出差都大半个月，前晚上女儿半夜高热我一个人火急火燎的送医院，打电话你还关机，天亮你也不回个电话。有你和没你有啥区别”\r\n&lt;/p&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;丈夫：&lt;/span&gt;“后来你告诉我，我这不是提前回来了嘛。”&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妻子：&lt;/span&gt;你真好意思说，说当天回来又拖到今天。你以后干脆别回家算了。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#60D978;&quot;&gt;丈夫：&lt;/span&gt;哎呀我实在是走不开，那边业务都还没有谈完。再说，家里不是还有你嘛。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妻子：&lt;/span&gt;没有谈完，你继续谈啊，还死回来干嘛？&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#60D978;&quot;&gt;丈夫：&lt;/span&gt;呵，你这话说的真难听。行，那我继续回去谈，还真的是没有忙完。（转身做出欲出门的样子）。&lt;br /&gt;\r\n（女儿站起来）&lt;span style=&quot;background-color:#FFE500;&quot;&gt;女儿&lt;/span&gt;上来，去拉爸爸：爸爸，别走了，我好想你。我不要你走。你走了，妈妈又要摔东西，骂我。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;去你妈妈那边吧，爸爸忙。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;女儿&lt;/span&gt;去拉住妈妈哀求道：妈妈，妈妈，别生气了，我好好听话，考试考第一名。妈妈。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妻子：&lt;/span&gt;你倒是考第一给我看，千年老二，还好意思说。你要是能让我省点心，我也好过些。&lt;br /&gt;\r\n（&lt;span style=&quot;background-color:#FFE500;&quot;&gt;女儿&lt;/span&gt;退到场中间，蹲着地上，无助的哭泣）&lt;br /&gt;\r\n画面定格。&lt;br /&gt;\r\n&lt;br /&gt;\r\n（咨询师出场）&lt;br /&gt;\r\n咨询师：这就是发生在他们家里的故事，接下来我通过家庭雕塑的方式来呈现给大家。&lt;br /&gt;\r\n来，孩子，我们现在来做一个雕塑，雕塑就好像拍一张照片一样。照片里面每个人一定有一个姿态的，通过这个姿态我们就知道： 当家庭有冲突的时候，他们的姿态是什么。他们的姿态可能是指责，指责是这样的（老师示范），或者是讨好（老师示范讨好），或者是打岔（示范），或者超理智，就是只是讲大道理（示范）。我说的明白吗？孩子。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;（点点头）明白。&lt;br /&gt;\r\n咨询师：好，所以在你的家里，当有冲突的时候，爸爸的姿态是什么？（指导配合）他的手呢？就是这样，比较有距离的意思？（摆出爸爸背过去，距离略远的姿态，手往后推）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;恩。&lt;br /&gt;\r\n咨询师：妈妈呢？爸爸这样的时候，妈妈会怎么做？ （孩子摆出指责的姿态）。&lt;br /&gt;\r\n妈妈会不会更高一点的感觉？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;是的。&lt;br /&gt;\r\n咨询师：（咨询师起身把椅子拿来，妈妈站到椅子上，两手分别指着丈夫和孩子）妈妈就是这样指责你们的是吗？她们的距离可以吗？（有点远的距离）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;恩。&lt;br /&gt;\r\n咨询师：孩子，当爸爸妈妈这样的时候，你会怎么做？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;（摆出讨好两人的姿势）&lt;br /&gt;\r\n咨询师：你觉得自己是这样的，同时讨好他们？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;（点头）恩。&lt;br /&gt;\r\n咨询师：我请大家在这个姿态里面停留一分钟，看看自己的内在，需要大家注意的是，我在这个家里，看到每个人是这样的。我的感受是什么？想的是什么。&lt;br /&gt;\r\n&lt;br /&gt;\r\n（停留大约一分钟后）&lt;br /&gt;\r\n&lt;br /&gt;\r\n咨询师（走向孩子）：孩子，这就是你现在的家，你这样讨好他们，你的感受是什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;我感到很难过，我想去帮助他们，但是很无力。&lt;br /&gt;\r\n咨询师：你说难过，那种难过是什么样的？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;觉得看不到希望。&lt;br /&gt;\r\n咨询师：恩 ，这是一种痛苦吗，还是悲伤，还是什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;是一种悲伤。&lt;br /&gt;\r\n咨询师：如果有一句话你想讲的话，你想讲什么?&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;我想说，你们能不能不要吵架了，能不能给我一个幸福的家？&lt;br /&gt;\r\n咨询师：如果在这两人中你选一个，你想和谁说？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;和妈妈讲。&lt;br /&gt;\r\n&lt;br /&gt;\r\n（咨询师走向妈妈）&lt;br /&gt;\r\n&lt;br /&gt;\r\n咨询师：妈妈，你的孩子想和你说话，你可以看着孩子。孩子，把你刚才想说的告诉妈妈。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;妈，每次看到你和爸爸吵架时候，我的内心很难过。我很希望你们不要吵，我希望自己可以阻止你们吵架，但是我没有能力做到。（断断续续的说）我希望我能有完整幸福的家。&lt;br /&gt;\r\n咨询师：妈妈，当你听到女儿这么说的时候，你想对她说什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;看到孩子这样，我很心痛，也觉得我没给她一个很好的家。&lt;br /&gt;\r\n咨询师：来，直接和女儿说。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;女儿，看到你这样，我很心痛，觉得我没给你一个很好的家。&lt;br /&gt;\r\n咨询师：如果有一句话，你很想说，因为她很难过，所以，你想对她说什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;妈妈会努力和爸爸搞好关系，给你一个幸福的家。我们一定努力重要做。&lt;br /&gt;\r\n&lt;br /&gt;\r\n（咨询师走向孩子）&lt;br /&gt;\r\n咨询师：妈妈说的话你相信吗？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt; 我愿意相信&lt;br /&gt;\r\n咨询师：那你告诉妈妈，我愿意相信。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;妈，你刚才说的我愿意相信你。&lt;br /&gt;\r\n&lt;br /&gt;\r\n（走向妈妈）&lt;br /&gt;\r\n咨询师：妈妈，你站在这么高的位置，而且你的女儿看到你是指责的。你的感受是什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;我是觉得自己有不对，每次都这样居高临下。&lt;br /&gt;\r\n咨询师：所以你有觉得自己是不对的。其实很多人看到的就是你的指责，但是我相信，在你的内在里面，一定也有很想说的话，但是从来没有说出来，那么你想说什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;（沉默了一会）其实我是很爱这个家的。有时候不知道怎么表达。有的时候一看到你哪里不好，就觉得没有达到我的要求，我的愤怒就上来了。&lt;br /&gt;\r\n咨询师：如果只能讲一句话，你想讲什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;我以后不会什么事都是指责。&lt;br /&gt;\r\n咨询师：这句话你要对谁说？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;我会和丈夫讲。&lt;br /&gt;\r\n&lt;br /&gt;\r\n（咨询师走向丈夫）&lt;br /&gt;\r\n咨询师：爸爸，我现在请你转回来，因为你的妻子有话和你说。你听听妻子的话，看看你的感受是什么？ （转向妻子） 妈妈，现在把你刚才那句话和他讲。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;老公，以后我不会遇到什么都不自我检讨，都是指责你，这样是不好的。我会去改。&lt;br /&gt;\r\n咨询师：为什么你愿意这样说？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;因为我觉得孩子这样说，是因为首先我和老公的关系出了问题。孩子夹在中间很难过，对孩子的指责是因为对老公有情绪。&lt;br /&gt;\r\n咨询师：所以，你这样说，你内在的渴望是什么？希望他知道什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;（陷入思考）我是想说，我是希望和你有连接。其实你身上有很多好的，我没有看到，看到的都是坏的。&lt;br /&gt;\r\n咨询师：那你愿意告诉他吗？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;老公，每次遇到事情，情绪一上来的时候，我从来不知道去肯定你认同你，你对家里付出很多，我知道你也很辛苦。可是我情绪一上来，就觉得你做的都是应该的，什么事不对了都是你的错。我想说我是很爱这个家的，很爱你和孩子。我希望我们一起把家经营好。&lt;br /&gt;\r\n咨询师：你只需要告诉他一句话：我想和你连接。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;我想和你连接。&lt;br /&gt;\r\n（问爸爸）&lt;br /&gt;\r\n咨询师：当你听到你妻子这样讲，你的感受是什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;刚开始觉得她不够真诚，但后来我慢慢能感受到她的真诚了，然后我心里想要和她连接，想把家经营好的感觉就有了。&lt;br /&gt;\r\n咨询师：那你收到了什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;收到她对我开始有肯定的感觉。&lt;br /&gt;\r\n咨询师；你可以告诉她吗？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;老婆，当我听你这样说，我能感觉到你对我的肯定，我感觉心里很舒服。我会慢慢把家经营好。&lt;br /&gt;\r\n咨询师：当你的女儿这样雕塑你的时候，你的感受是什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;我觉得自己好像是一个局外人，在家里没有感受到老婆的爱。女儿对我有期待，但我的心不在家里，是被老婆赶走的感觉。我很孤单。&lt;br /&gt;\r\n咨询师：当你说感到孤单的时候，你的期待是什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;期待和家里有连接，能够有那种家的温暖。&lt;br /&gt;\r\n咨询师：这句话你想和谁说？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;我更想对孩子说。&lt;br /&gt;\r\n咨询师：那对女儿说吧。&lt;br /&gt;\r\n&lt;br /&gt;\r\n（爸爸转向女儿）&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;女儿，之前爸爸一直对这个家不太负责任，没有付出太多，是爸爸不对。我希望在以后的日子里，我们一家人能多些连接，多在一起。&lt;br /&gt;\r\n（咨询师走向孩子）&lt;br /&gt;\r\n咨询师：孩子，当听到爸爸这样说的时候，你想对他说什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;我听你这样说的时候，我半信半疑。&lt;br /&gt;\r\n咨询师：不太相信是吗？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;是的。&lt;br /&gt;\r\n&lt;br /&gt;\r\n咨询师：好，孩子，我现在有机会让你做一个你想要的家，我们不知道这样的家能不能做到，但是孩子，我想让你知道的是，今天爸爸妈妈是为了这个家一起来到这里，他们也不容易，要放下时间和工作，还有其他。如果有一天，你理想的家有了，你会怎么雕塑他们？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;孩子：&lt;/span&gt;（让妈下来。爸爸来过来，孩子在中间，大家手拉手一起。）&lt;br /&gt;\r\n咨询师：妈妈，如果你理想的家有了，那是怎样的？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;妈妈：&lt;/span&gt;这样就好。（三人手拉手！）&lt;br /&gt;\r\n咨询师：爸爸，如果你理想的家有了，那是怎样的？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#60D978;&quot;&gt;爸爸：&lt;/span&gt;就是这个样子，还可以再靠近一点。&lt;br /&gt;\r\n&lt;br /&gt;\r\n咨询师： 原来这才是她们每个人心里想要的家。还记得他们最开始的姿态吗？所以，我们看到的是家庭中的每个人在同样的冲突下两种不同的姿态。为什么会如此的不同呢？（略停）&lt;br /&gt;\r\n因为在最初的姿态里，他们的沟通都没有说出自己真正的想法，他们展示出的并不是真正的自己，而是一个内心受伤的自己。而当我们可以真诚一致的表达自己时，对方就会也越来越真诚一致。隐藏在彼此在心底的爱就会浮上来，家庭关系就会越来越幸福和谐，这就是一个家庭的内部动力。&lt;br /&gt;\r\n在此，我也祝愿在座的每个人，都能家庭幸福，和谐美满。&lt;br /&gt;\r\n谢谢大家！&lt;br /&gt;\r\n&lt;br /&gt;\r\n在一个家庭里，当父母之间不能真诚一致的沟通时，对孩子的影响是特别大的。因为孩子感受到了危险，可她并不知道问题出在哪里？事实上，我们只要把自己内心真实的想法说出来，可以表达自己的愤怒和悲伤，并说出隐藏在愤怒和悲伤背后的渴望。 &lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	其实，家庭里的每个人，都带着对彼此的深深的期待和要求，如果一旦不能满足，我们就会失望，继而生气。所以当我们想表达的时候，内心都被负面情绪所占据，就无法表达真实的自己了。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;我爱我的家&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}\r\n&lt;/p&gt;\r\n&lt;div style=&quot;white-space:nowrap;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;',	'',	'',	2),
(40,	74,	'丑小鸭',	'',	'',	'',	0,	0,	10,	1,	1,	'2019-06-06 23:37:40',	'2019-06-11 17:24:56',	'',	'背景：湖边小木屋前草地上&lt;br /&gt;\r\n人物：鸭妈妈、三个鸭宝宝、丑小鸭&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	《摇篮曲》音乐起\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;丑小鸭-1-勃拉姆斯-摇篮曲&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	湖边草地上鸭妈妈正要孵出她的小鸭子来，鸭妈妈按摸着鸭蛋。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;我就要做妈妈了，我的孩子就要出世了，1，2，3，4 我就是4个孩子的妈妈了，我多么光荣多么伟大，对了，我先给他们准备好吃的。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	（妈妈下场）\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;丑小鸭-2-蛋壳破碎声音&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	一个蛋壳裂开来小鸭跳出来。\r\n&lt;/p&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;哎呀，呦，这什么地方，天是这么蓝，水是这么清，是我的家吗，妈妈！妈妈！妈妈！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（小鸭四处找妈妈，妈妈上）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;老大，我的孩子你出世了，妈妈在这里！快到妈妈这里来。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;妈妈！妈妈！（奔向妈妈怀里）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（鸭蛋一个接一个裂开，小鸭一个一个蹦出来。）&lt;br /&gt;\r\n众小鸭：妈妈！妈妈！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;我亲爱的孩子们，欢迎你们来到这个世界上，众小鸭跑过来围着妈妈。&lt;br /&gt;\r\n众小鸭：妈妈！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;现在让妈妈为你们洗澡。（众小鸭欢跳，摇摇摆摆）&lt;br /&gt;\r\n众小鸭：好！好！好！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#DFC5A4;&quot;&gt;鸭老二：&lt;/span&gt;我是老二！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;鸭老三：&lt;/span&gt;我是老三！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;别急，大家先排好队，我来点，老大！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;到！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;老二！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#DFC5A4;&quot;&gt;鸭老二：&lt;/span&gt;到！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;老三！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#B8D100;&quot;&gt;鸭老三：&lt;/span&gt;到！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;老四！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;咦？老四呢？（分头找）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;老四？&lt;br /&gt;\r\n众小鸭：妈妈！这里还有一个蛋。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;对！是我们老四。（众鸭围着鸭蛋，鸭妈妈抚摸着鸭蛋）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;孩子别着急，别担心，妈妈来给你温暖，妈妈来给你力量。（众鸭生气走开）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;哼！妈妈不管我，我们怎么办？&lt;br /&gt;\r\n众小鸭：我们怎么办？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;孩子们，老四最小还没有出世，最需要妈妈的照顾，没有妈妈的温暖她是无法来到这个世界上的！&lt;br /&gt;\r\n众小鸭：噢！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;老大！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;哎，妈妈！&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;你带着弟弟妹妹们去吃妈妈为你们准备好的点心，吃饱了就让他们去玩吧。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;丑小鸭-3-余日秋山-儿时的夏日&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}\r\n&lt;/p&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;走！弟弟妹妹们跟我走！&lt;br /&gt;\r\n众小鸭：再见！妈妈。（鸭妈妈挥手再见，回到鸭蛋旁边爱抚地）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;好孩子，我的心肝宝贝，加把劲快钻出来吧，妈妈为你担心（鸭蛋动），孩子，你听到妈妈的话了吗？快啊！我的老四快要出世了，好孩子，妈妈好想你呀&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（众小鸭上）&lt;br /&gt;\r\n众小鸭：妈妈，天快黑了我们回家了，妈妈，没有你在旁边我们好害怕。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;孩子们，天黑了就到妈妈身边来，妈妈陪你们睡觉。&lt;br /&gt;\r\n众小鸭：哦！妈妈给我们讲故事。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;好的。（妈妈讲故事，众小鸭躺在妈妈身边，慢慢地都睡着了。丑小鸭破壳而出。）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;好美的地方啊！高高的树林，清清的河水，绿盈盈的草地，还有可爱的小木屋和幸福美满的这一家，我的家呢？我的妈妈呢？妈妈！妈妈！（妈妈和众小鸭醒）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:normal;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;我的孩子出世了，孩子！妈妈在这里吶，快到这里来！&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（丑小鸭奔向妈妈怀里，众小鸭从头到脚打量着丑小鸭）&lt;br /&gt;\r\n众小鸭：哈哈！这么丑，妈妈！这哪里是我们的妹妹呀，毛灰灰的，这么难看怎么能和我们比呢？头又大又黑......，我们才不要。这个丑八怪妹妹呢，不要就是不要。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;孩子们，他是丑了一点，但他真是你们的小妹妹，同一个窝里生出来的，她就是你们的小妹妹，你们不能欺负她。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;可怜的小妹妹帮你七个名字，（和鸭耳语）叫？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#DFC5A4;&quot;&gt;鸭老二：&lt;/span&gt;叫什么？叫什么？（鸭老大想了一会儿）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;丑小鸭！（众小鸭欢呼）&lt;br /&gt;\r\n众小鸭：丑小鸭！丑小鸭！丑小鸭！丑小鸭！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;丑小鸭，这个名字我喜欢。&lt;br /&gt;\r\n众小鸭：又黑又丑不脸红，还要说我喜欢，我喜欢！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;孩子们，一个人的美丽不能只看外表，而在内心，丑小鸭这个名字很特别，我也喜欢，嗯，就叫丑小鸭吧，好了好了，孩子们你们好好玩吧，妈妈去为你们准备，（众小鸭凑上前）&lt;br /&gt;\r\n众小鸭：准备什么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;好吃的。&lt;br /&gt;\r\n众小鸭：噢！（众小鸭欢跳，丑小鸭找哥哥姐姐玩，众小鸭不理她）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;大姐，大姐我是你们的小妹妹，别笑我。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#DFC5A4;&quot;&gt;鸭老二：&lt;/span&gt;去去去，你离我远一点，你根本不是我们的小妹妹。（丑小鸭奔向另一个哥哥）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;大哥大哥别嫌弃我，我不过长得丑了一点，样子和大家长得差不多么。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;不知道，还喊我大哥，真讨厌，我不喜欢（边说边推丑小鸭）我就是不喜欢。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;弟弟妹妹们，走！我们一起去玩。&lt;br /&gt;\r\n众小鸭：噢！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;哥哥姐姐带上我，别把我落下。&lt;br /&gt;\r\n&lt;p&gt;\r\n	众小鸭：哼！别跟着我们，还和我们争妈妈，真不要脸。（众小鸭嬉笑着离开丑小鸭）\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;丑小鸭-4-高梨康治-宿命&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;因为我长的丑，哥哥姐姐不要我，只有妈妈疼我，他们都说我和大家争妈妈，还骂我不要脸，我该怎么办？我该怎么办？（哭）妈妈，妈妈，妈妈我走了，哥哥姐姐再见了，妈妈我走了，哥哥姐姐再见了。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;丑小鸭-5-雷电下雨声效音&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt;&lt;span style=&quot;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;white-space:normal;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	（丑小鸭奔下）电闪雷鸣，风雨交加，在空旷的野外，丑小鸭孤独地挣扎，搏斗。\r\n&lt;/p&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;哥哥姐姐救救我！妈妈你在哪里啊？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（鸭妈妈带着众小鸭）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;丑小鸭！我可怜的孩子！你在哪儿啊？&lt;br /&gt;\r\n众小鸭：快回家吧！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;丑小鸭！我可怜的孩子！&lt;br /&gt;\r\n众小鸭：妹妹！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;你再哪儿哪？&lt;br /&gt;\r\n众小鸭：快回家吧！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;丑小鸭！&lt;br /&gt;\r\n众小鸭：丑小鸭！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;我可怜的孩子！&lt;br /&gt;\r\n众小鸭：妹妹！快回家吧！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;你在哪儿哪？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（寒冬过去了，春天又来了，太阳再次暖暖地照射大地。）&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（音乐起，小天鹅翩翩起舞。）\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;white-space:normal;&quot;&gt;{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;丑小鸭-6-Felix-春之歌&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}&lt;/span&gt;&lt;span style=&quot;white-space:normal;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;white-space:normal;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;咦？水里的倒影是我丑小鸭吗？我怎么变得这样美丽了，哥哥姐姐一定会喜欢我的，妈妈一定会更疼我的，对！我要回家！我要回家！（丑小鸭下场）&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（鸭妈妈拉着众小鸭）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;丑小鸭你在哪儿啊？我可怜的孩子，你让我们到哪里去找你啊？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（鸭妈妈伤心地累倒在地，众小鸭扑向妈妈）&lt;br /&gt;\r\n众小鸭：妈妈你不要，妈妈你不要再难过了，妈妈没有丑小鸭还有我们大家呢，妈妈你不要不理我们，我们也要妈妈。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#DFC5A4;&quot;&gt;鸭老二：&lt;/span&gt;也许丑小鸭早被大灰狼吃了。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;胡说！你们可怜的妹妹，我的丑小鸭！（说着说着又昏倒，众小鸭围着妈妈）&lt;br /&gt;\r\n众小鸭：妈妈！妈妈！（丑小鸭上）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;妈妈，哥哥姐姐，我回来了！&lt;br /&gt;\r\n众小鸭：你是谁呀？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#00D5FF;&quot;&gt;鸭老大：&lt;/span&gt;好看的羽毛，比我们的美丽多了。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;哥哥姐姐你们不认识我了，我是丑小鸭呀！&lt;br /&gt;\r\n众小鸭：丑小鸭？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;哎呀！妈妈怎么了？&lt;br /&gt;\r\n众小鸭：妈妈想你了，整天惦记你，不吃也不喝，她病倒了。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#DFC5A4;&quot;&gt;鸭老二：&lt;/span&gt;妈妈想你了，整天哭，她的眼睛都哭瞎了。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（丑小鸭扑向妈妈。）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;妈妈你快醒醒，睁大眼睛看看，你的丑小鸭回来了。&lt;br /&gt;\r\n众小鸭：妈妈，你睁大眼睛看看，我们的鸭妹妹回家了。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（妈妈慢慢起身抬起头）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;我的丑小鸭，你在哪里？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;妈妈我在这里，我就是你的丑小鸭呀！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;你就是我的丑小鸭？&lt;br /&gt;\r\n众小鸭：是的。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（妈妈奔过去把丑小鸭抱在怀里）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;好孩子，妈妈想死你了。&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;我也想妈妈，想哥哥姐姐。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（众小鸭牵着手，围着丑小鸭欢跳）&lt;br /&gt;\r\n众小鸭：噢！鸭妹妹回来喽，我们一家又团聚了。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（鸭妈妈搂着丑小鸭）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;不！孩子们，小妹妹不是鸭子，是白天鹅。&lt;br /&gt;\r\n众小鸭：白天鹅？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;她应该回到天鹅湖去。&lt;br /&gt;\r\n众小鸭：为什么？&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（丑小鸭推开鸭妈妈）&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;妈妈你怎么了？为什么又不要我了？哥哥姐姐你们还嫌弃我么？&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#EE33EE;&quot;&gt;鸭妈妈：&lt;/span&gt;呆在草堆里啊，要弄脏你洁白的羽毛，降低你尊贵的身份的！&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFE500;&quot;&gt;丑小鸭：&lt;/span&gt;不！我是鸭妈妈孵出来的丑小鸭，是哥哥姐姐的好妹妹。（拉着众小鸭的手）我们永远在一起，从此再也不分离。&lt;br /&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（众小鸭拉着手围着丑小鸭欢跳）&lt;br /&gt;\r\n&lt;p&gt;\r\n	众小鸭：对！我们是亲亲热热一家人，我们永远在一起，从此再也不分离。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	{{&quot;type&quot;:&quot;audio&quot;,&quot;name&quot;:&quot;丑小鸭-7-龚玥-相亲相爱的一家人&quot;,&quot;start&quot;:0,&quot;stop&quot;:-1,&quot;repeat&quot;:0}}\r\n&lt;/p&gt;\r\n&lt;span style=&quot;white-space:pre;&quot;&gt; &lt;/span&gt;（丑小鸭搂着妈妈，众小鸭抱在一起）&lt;br /&gt;',	'',	'',	8),
